const fs = require('fs');
const path = require('path');

const videoDir = '/videos/subway-sounds';
const doneDir = path.join(videoDir, 'done');
const extensions = ['.mp4', '.mov'];

// Ensure done/ subfolder exists
if (!fs.existsSync(doneDir)) {
  fs.mkdirSync(doneDir, { recursive: true });
}

const files = fs.readdirSync(videoDir).filter(f => {
  const ext = path.extname(f).toLowerCase();
  return extensions.includes(ext);
});

const results = [];

for (const file of files) {
  const filePath = path.join(videoDir, file);
  const baseName = path.basename(file, path.extname(file));
  const sidecarPath = path.join(videoDir, baseName + '.json');

  let title = baseName.replace(/[-_]/g, ' ').replace(/\b\w/g, c => c.toUpperCase());
  let description = `NYC subway sounds from ${baseName.replace(/[-_]/g, ' ')}. Subscribe for more authentic subway audio.`;
  let tags = ['nyc subway', 'subway sounds', 'MTA'];
  let privacy = 'public';
  let category = '22';

  if (fs.existsSync(sidecarPath)) {
    try {
      const meta = JSON.parse(fs.readFileSync(sidecarPath, 'utf8'));
      if (meta.title) title = meta.title;
      if (meta.description) description = meta.description;
      if (meta.tags) tags = meta.tags;
      if (meta.privacy) privacy = meta.privacy;
      if (meta.category) category = meta.category;
    } catch (e) {
      // ignore bad JSON, use defaults
    }
  }

  results.push({
    json: { filePath, file, baseName, sidecarPath, title, description, tags, privacy, category }
  });
}

return results.length > 0 ? results : [{ json: { noFiles: true } }];

const fs = require('fs');
const path = require('path');

const filePath = $input.first().json.filePath;
const file = $input.first().json.file;
const baseName = $input.first().json.baseName;
const videoDir = path.dirname(filePath);
const doneDir = path.join(videoDir, 'done');

// Move video file
fs.renameSync(filePath, path.join(doneDir, file));

// Move sidecar JSON if it exists
const sidecarPath = path.join(videoDir, baseName + '.json');
if (fs.existsSync(sidecarPath)) {
  fs.renameSync(sidecarPath, path.join(doneDir, baseName + '.json'));
}

return [{ json: { moved: file, to: doneDir } }];
